import React, { useEffect, useState } from 'react'
import axios from 'axios';

const TableEmployee = () => {
    const [employees, setEmployees] = useState([])
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [number, setNumber] = useState(' ')
    const [editId, setEditID] = useState(-1)
    useEffect(  async () => {
        await axios.get('https://a247-117-2-6-32.ngrok-free.app/employee',
            {
                headers: {
                    "Accept": 'application/json',
                    'ngrok-skip-browser-warning': 'any',
                    "Content-Type": "application/x-www-form-urlencoded",
                    "Access-Control-Allow-Origin": "*",


                },
                withCredentials: true
            }
        )
            .then((res) => {
                setEmployees(res.data.employees)
                console.log("Dữ liệu của emoloyess là:",employees)
                console.log(res.data)
                console.log("data is: ", res.data)
            })
            .catch(err => console.log(err))

    }, []);
    const handleSubmit = (event) => {
        event.preventDefault();
        const id = employees.length + 1;
        axios.post('https://a247-117-2-6-32.ngrok-free.app/employee', { employees_id: id, employees_name: name, email: email, phone_number: number })
            .then(res => setEmployees(res.data))
            .catch(err => console.log(err))
    }
    const handleEdit = (id) => {
        setEditID(id)
    }
    const handleUpdate = () => {
        axios.put('https://a247-117-2-6-32.ngrok-free.app/employee' + editId, { employees_id: editId, employees_name: name, email: email, phone_number: number })
            .then(res => {
                console.log(res);
                setEditID(-1);
            }).catch(err => console.log(err));
    }
    const handleDelete = (event) => {
        //  event.preventDefault();
        axios.delete('https://a247-117-2-6-32.ngrok-free.app/employee' + editId, { employees_id: editId, employees_name: name, email: email, phone_number: number })
    }

    return (
        <div className='container'>
            <div className='form-div'>
                <form onSubmit={handleSubmit} action='' >
                    <input type='text' placeholder='Enter Name ' onChange={e => setName(e.target.value)} />
                    <input type='text' placeholder='Enter Email' onChange={e => setEmail(e.target.value)} />
                    <input type='text' placeholder='Enter Phone Number' onChange={e => setNumber(e.target.value)} />
                    <button>Add</button>
                </form>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone Number</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((employee, key) => {
                        <tr key={key}>
                            <td>{employee.employees_id}</td>
                        </tr>
                    })}

                    
                </tbody>
            </table>
        </div>
    )
}

export default TableEmployee
