import React, { useEffect, useState } from 'react'
import axios from 'axios';

const TableEmployee = () => {
    const [employees, setEmployees] = useState([1,2,3])
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [number, setNumber] = useState(' ')
    const [editId, setEditID] = useState(-1)
    useEffect(() => {
        axios.get('https://my.api.mockaroo.com/employee.json?key=a7202480',
            {
                headers: {
                    "Content-Type": "application/json",
                    "Accept": 'application/json',
                }
            }
        )
            .then((res) => {
                setEmployees(res.data)
                console.log("data is: ", res.data)
            })
            .catch(err => console.log(err))

    }, []);
    const handleSubmit = (event) => {
        event.preventDefault();
        const id = employees.length + 1;
        axios.post('https://a247-117-2-6-32.ngrok-free.app/employee', { employees_id: id, employees_name: name, email: email, phone_number: number })
            .then(res => setEmployees(res.data))
            .catch(err => console.log(err))
    }
    const handleEdit = (id) => {
        setEditID(id)
    }
    const handleUpdate = () => {
        axios.put('https://a247-117-2-6-32.ngrok-free.app/employee' + editId, { employees_id: editId, employees_name: name, email: email, phone_number: number })
            .then(res => {
                console.log(res);
                setEditID(-1);
            }).catch(err => console.log(err));
    }
    const handleDelete = (event) => {
        //  event.preventDefault();
        axios.delete('https://a247-117-2-6-32.ngrok-free.app/employee' + editId, { employees_id: editId, employees_name: name, email: email, phone_number: number })
    }

    console.log(employees)
    return (
        <div className='container'>
            <div className='form-div'>
                <form onSubmit={handleSubmit} action='' >
                    <input type='text' placeholder='Enter Name ' onChange={e => setName(e.target.value)} />
                    <input type='text' placeholder='Enter Email' onChange={e => setEmail(e.target.value)} />
                    <input type='text' placeholder='Enter Phone Number' onChange={e => setNumber(e.target.value)} />
                    <button>Add</button>
                </form>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone Number</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {/* {
                        employees.map(employee => (
                            employee.employees_id === editId ?(
                                <tr>
                                    <td>{employee.employees_id}</td>
                                    <td><input type='text' value={employee.employees_name} onChange={e => setName(e.target.value)} /></td>
                                    <td><input type='text' value={employee.email} onChange={e => setEmail(e.target.value)} /></td>
                                    <td><input type='text' value={employee.phone_number} onChange={e => setNumber(e.target.value)} /></td>
                                    <td><button onClick={handleUpdate}>Update</button></td>
                                </tr>
                                ) : (
                                <tr >
                                    <td>{employee.employees_id}</td>
                                    <td>{employee.employees_name}</td>
                                    <td>{employee.email}</td>
                                    <td>{employee.phone_number}</td>
                                    <td>
                                        <button onClick={() => handleEdit(employee.employees_id)}>Edit</button>
                                        <button onClick={handleDelete(employee.employees_id)}>Delete</button>
                                    </td>
                                </tr>
                        )))
                    } */}
                    {employees.map(employee => (
                        <tr key={employee.employees_id}>
                            <td>{employee.employees_id}</td>
                            <td>{editId === employee.employees_id ? (
                                <input type='text' value={name} onChange={e => setName(e.target.value)} />
                            ) : (
                                employee.employees_name
                            )}
                            </td>
                            <td>{editId === employee.employees_id ? (
                                <input type='text' value={email} onChange={e => setEmail(e.target.value)} />
                            ) : (
                                employee.email
                            )}
                            </td>
                            <td>{editId === employee.employees_id ? (
                                <input type='text' value={number} onChange={e => setNumber(e.target.value)} />
                            ) : (
                                employee.phone_number
                            )}
                            </td>
                            <td>
                                {editId === employee.employees_id ? (
                                    <button onClick={handleUpdate}>Save</button>
                                ) : (
                                    <button onClick={() => handleEdit(employee.employees_id)}>Edit</button>
                                )}
                                <button onClick={() => handleDelete(employee.employees_id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}

export default TableEmployee
