import React, { useEffect, useState } from 'react'
import axios from 'axios';
const TableEmployee = () => {
    const [data, setData] = useState([])
    const [name, setName] = useState('')
    const [editId, setEditID] = useState(-1)
    useEffect(() => {
        axios.get('https://a247-117-2-6-32.ngrok-free.app/employee')
            // .then(res => console.log(res.data)) 
            // .catch(er => console.error(er))
            .then(function (response) {
                console.log('Send REST Api result: ');
                let dataResponse = response.data;
                if (dataResponse != null) {
                    this.setState({                            
                        codeResult: JSON.stringify(dataResponse),
                    })
                    console.log(JSON.stringify(dataResponse));
                }
            })
            .catch(function (error) {
                console.log('error request api');
                console.log('NetworkStatus: ' + error.toString());
                let statusText = '';
                try {
                    statusText = error.response.statusText;
                } catch (e) {

                }
                console.log(error.toString() + ".\n" + statusText);
            });


    }, []);
    // useEffect(() => {
    //     fetchRooms();
    //   }, []);
    
    
    //   const fetchRooms = async () => {
    //     try {
    //       const response = await fetch('https://a247-117-2-6-32.ngrok-free.app/employee'); 
    //       const data = await response.json();
    //       setData(data);
    //     } catch (error) {
    //       console.('Error fetching rooms:', error);
    //     }
    //   };
    const handleSubmit = (event) => {
        event.preventDefault();
        const id = data.length + 1;
        axios.post('https://a247-117-2-6-32.ngrok-free.app/employee', { employees_id: id, emmployees_name: name })
            .then(res =>setData(res.data))
            .catch(err => console.log(err))

    }
    const handleEdit = (id) => {
        setEditID(id)
    }
    const handleUpdate = ()=>{
        axios.put('https://a247-117-2-6-32.ngrok-free.app/employee'+editId, {id: editId, name})
        .then(res => {
            console.log(res);
            setEditID(-1);

        }).catch(err =>console.log(err));
    }
    const handleDelete = ()=>{
         
    }
    return (
        <div className='container'>
            <div className='form-div'>
                <form onSubmit={handleSubmit} action=''>
                    <input type='text' placeholder='Enter Name ' onChange={e => setName(e.target.value)} />
                    <button>Add</button>
                </form>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((user, index) => (
                            user.employees_id === editId ?
                                <tr>
                                    <td>{user.employees_id}</td>
                                    <td><input type='text' value={user.emmployees_name} onChange={e=> setName(e.target.value)}/></td>
                                    <td><button onClick={handleUpdate}>Update</button></td>
                                </tr>
                                :
                                <tr key={index}>
                                    <td>{user.employees_id}</td>
                                    <td>{user.employees_name}</td>
                                    <td>
                                        <button onClick={() => handleEdit(user.employees_id)}>Edit</button>
                                        <button onClick={handleDelete(user.employees_id)}>Delete</button>
                                    </td>
                                </tr>
                        ))
                    }
                </tbody>
            </table>
        </div>
    )
}

export default TableEmployee
