import React, { useEffect, useState } from 'react'
import axios from 'axios';
const TableEmployee = () => {
    const [data, setData] = useState([])
    const [name, setName] = useState('')
    const [editId, setEditID] = useState(-1)
    useEffect(() => {
        axios.get('https://a247-117-2-6-32.ngrok-free.app/employee')
            .then( res =>{
                res.data
                setData({data})
            })
            .catch(err => console.log(err))

    }, []);
   
    const handleSubmit = (event) => {
        event.preventDefault();
        const id = data.length + 1;
        axios.post('https://a247-117-2-6-32.ngrok-free.app/employee', { employees_id: id, emmployees_name: name })
            .then(res =>setData(res.data))
            .catch(err => console.log(err))

    }
    const handleEdit = (id) => {
        setEditID(id)
    }
    const handleUpdate = ()=>{
        axios.put('https://a247-117-2-6-32.ngrok-free.app/employee'+editId, {id: editId, name})
        .then(res => {
            console.log(res);
            setEditID(-1);

        }).catch(err =>console.log(err));
    }
    const handleDelete = (event )=>{
         event.preventDefault();
         axios.delete('https://a247-117-2-6-32.ngrok-free.app/employee'+ editId,{id: editId, name})
    }
    return (
        <div className='container'>
            <div className='form-div'>
                <form onSubmit={handleSubmit} action=''>
                    <input type='text' placeholder='Enter Name ' onChange={e => setName(e.target.value)} />
                    <button>Add</button>
                </form>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((user, index) => (
                            user.employees_id === editId ?
                                <tr>
                                    <td>{user.employees_id}</td>
                                    <td><input type='text' value={user.emmployees_name} onChange={e=> setName(e.target.value)}/></td>
                                    <td><button onClick={handleUpdate}>Update</button></td>
                                </tr>
                                :
                                <tr key={index}>
                                    <td>{user.employees_id}</td>
                                    <td>{user.employees_name}</td>
                                    <td>
                                        <button onClick={() => handleEdit(user.employees_id)}>Edit</button>
                                        <button onClick={handleDelete(user.employees_id)}>Delete</button>
                                    </td>
                                </tr>
                        ))
                    }
                </tbody>
            </table>
        </div>
    )
}

export default TableEmployee
