import React, { useEffect, useState } from 'react'
import axios from 'axios';
import {FiEdit3} from 'react-icons/fi'
import {RiDeleteBin6Fill} from 'react-icons/ri'

const TableEmployee = () => {
    const [employees, setEmployees] = useState([])
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [number, setNumber] = useState(' ')
    const [editId, setEditID] = useState(-1)


    const fetchEmployees = async () => {

        try {
            const response = await axios.get('https://a247-117-2-6-32.ngrok-free.app/employee',
                {
                    headers: {
                        "Accept": 'application/json',
                        'ngrok-skip-browser-warning': 'any',
                        "Content-Type": "application/x-www-form-urlencoded",
                        "Access-Control-Allow-Origin": "*",
                    },
                    withCredentials: true
                }
            )
            console.log(response.data.employee);
            setEmployees(response.data.employee);
            console.log(employees);
            Window.location.reload();

        } catch (error) {
        }
    }

    useEffect(() => {
        fetchEmployees()
    }, []);

    
    const handleSubmit = (event) => {
        event.preventDefault();
        const id = employees.length + 1;
        axios.post('https://a247-117-2-6-32.ngrok-free.app/employee', { employees_id: id, employees_name: name, email: email, phone_number: number })
            .then(res => {
                setEmployees(res.data.employee)
                // location.reload()
            })
            .catch(err => console.log(err))
    }
    const handleEdit = (id) => {
        setEditID(id)
    }
    const handleUpdate = () => {
        axios.put('https://a247-117-2-6-32.ngrok-free.app/employee' + editId, { employees_id: editId, employees_name: name, email: email, phone_number: number })
            .then(res => {
                console.log(res);
                // location.reload();
                setEditID(-1);
            }).catch(err => console.log(err));
    }
    const handleDelete = (id) => {
        //  event.preventDefault();
        axios.delete('https://a247-117-2-6-32.ngrok-free.app/employee' + id, { employees_id: id, employees_name: name, email: email, phone_number: number })
        .then( res =>{
            // location.reload()
        })
        .catch(er=> console.log(er))
    }

    return (
        <div className='container'>
            <div className='form-div'>
                <form onSubmit={handleSubmit} action='' >
                    <input type='text' placeholder='Enter Name ' onChange={e => setName(e.target.value)} />
                    <input type='text' placeholder='Enter Email' onChange={e => setEmail(e.target.value)} />
                    <input type='text' placeholder='Enter Phone Number' onChange={e => setNumber(e.target.value)} />
                    <button onClick={handleSubmit}>Add</button>
                </form>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone Number</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((employee, key) => {
                        return(<>
                       
                    
                        <tr key={employee.employees_id}>
                        <td>{employee.employees_id}</td>
                        <td>{editId === employee.employees_id ? (
                            <input type='text' value={name} onChange={e => setName(e.target.value)} />
                        ) : (
                            employee.employees_name
                        )}
                        </td>
                        <td>{editId === employee.employees_id ? (
                            <input type='text' value={email} onChange={e => setEmail(e.target.value)} />
                        ) : (
                            employee.email
                        )}
                        </td>
                        <td>{editId === employee.employees_id ? (
                            <input type='text' value={number} onChange={e => setNumber(e.target.value)} />
                        ) : (
                            employee.phone_number
                        )}
                        </td>
                        <td>
                            {editId === employee.employees_id ? (
                                <button onClick={handleUpdate}>Update</button>
                            ) : (
                                <button onClick={() => handleEdit(employee.employees_id)}><FiEdit3 style={{color: "blue"}}/></button>
                            )}
                            <button onClick={() => handleDelete(employee.employees_id)}><RiDeleteBin6Fill style={{color: "red", marginLeft:12}}/></button>
                        </td>
                    </tr>
                    
                    </>)}
                    )}

                </tbody>
            </table>
        </div>
    )
}

export default TableEmployee
