import React, { useEffect, useState } from 'react'
import axios from 'axios';
const TableEmployee = () => {
    const [data, setData] = useState([])
    const [name, setName] = useState('')
    useEffect(() => {
        fetch('https://7d69-210-245-110-144.ngrok-free.app/employees')
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
            })
            .catch((err) => {
                console.log(err.message);
            });
    }, [])
    const handleSubmit = (event) => {
        event.preventDefault();
        const id = data.length + 1;
        axios.post('https://6054-210-245-110-144.ngrok-free.app/employee', {employees_id: id , emmployees_name: name})
        .them(res =>console.log(res))
        .catch(er => console.log())
    }
    return (
        <div className='container'>
            <div>
                <form onSubmit={handleSubmit} action=''>
                    <input type='text' placeholder='Enter Name ' onChange={e => setName(e.target.value)} />
                    <button>Add</button>
                </form>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((user, index) => {
                            <tr key={index}>
                                <td>{user.employees_id}</td>
                                <td>{user.employees_name}</td>
                                <td>
                                    <button>Edit</button>
                                    <button>Delete</button>
                                </td>
                            </tr>
                        })
                    }
                </tbody>
            </table>
        </div>
    )
}

export default TableEmployee
