import React, { useEffect, useState } from 'react'
import axios from 'axios';
const TableEmployee = () => {
    const [data, setData] = useState([])
    const [name, setName] = useState('')
    const [editId, setEditID] = useState(-1)
    useEffect(() => {
        axios.get('https://6054-210-245-110-144.ngrok-free.app/employee')
            .then(res => setData(res.data))
            .catch(er => console.log())
    }, [])
    const handleSubmit = (event) => {
        event.preventDefault();
        const id = data.length + 1;
        axios.post('https://6054-210-245-110-144.ngrok-free.app/employee', { employees_id: id, emmployees_name: name })
            // .then(res =>console.log(res))
            .then(res => { location.reload() })
            .catch(er => console.log())

    }
    const handleEdit = (id) => {
        setEditID(id)
    }
    return (
        <div className='container'>
            <div className='form-div'>
                <form onSubmit={handleSubmit} action=''>
                    <input type='text' placeholder='Enter Name ' onChange={e => setName(e.target.value)} />
                    <button>Add</button>
                </form>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((user, index) => {
                            user.id === editId ?
                                <tr>
                                    <td>{user.employees_id}</td>
                                    <td><input type='text' value={user.emmployees_name} /></td>
                                    <td><button>Update</button></td>
                                </tr>
                                :
                                <tr key={index}>
                                    <td>{user.employees_id}</td>
                                    <td>{user.employees_name}</td>
                                    <td>
                                        <button onClick={() => handleEdit(user.employees_id)}>Edit</button>
                                        <button>Delete</button>
                                    </td>
                                </tr>
                        })
                    }
                </tbody>
            </table>
        </div>
    )
}

export default TableEmployee
